import { binToDna, dnaToBin } from '../src/functions/binConverter';
import { expect } from 'chai';
import 'mocha';

describe('Binaries to DNA', () => {
  it('should return the right conversion', () => {
    const result = binToDna(
      '',
    );
    expect(result).to.equal(
      'TTGACCGATGACCCCGGTTCAGGCTTCACCACAGTGTGGAACGCGGTCGTCTCCGAACTTAACGGCGACCCTAAGGTTGACGACGGACCCAGCAGTGATGCTAATCTCAGCGCTCCGCTGACCCCTCAGCAAAGGGCTTGGCTCAATCTCGTCCAGCCATTGACCATCGTCGAGGGGTTTGCTCTGTTATCCGTGCCGAGCAGCTTTGTC',
    );
  });
});

describe('DNA to Binaries', () => {
  it('should return the right conversion', () => {
    const result = dnaToBin(
      'TTGACCGATGACCCCGGTTCAGGCTTCACCACAGTGTGGAACGCGGTCGTCTCCGAACTTAACGGCGACCCTAAGGTTGACGACGGACCCAGCAGTGATGCTAATCTCAGCGCTCCGCTGACCCCTCAGCAAAGGGCTTGGCTCAATCTCGTCCAGCCATTGACCATCGTCGAGGGGTTTGCTCTGTTATCCGTGCCGAGCAGCTTTGTC',
    );
    expect(result).to.equal(
      '',
    );
  });
});
